import React from "react";
import './App.css';
import Header from './Header';
import useAuth from './useAuth';
import {useState, useEffect} from 'react'
import { Routes, Route, Link } from "react-router-dom";
import SpotifyWebApi from "spotify-web-api-node";
import TextField from '@mui/material/TextField';

const spotifyApi = new SpotifyWebApi({
    clientId: "c176bd9d61984d3f8c4804b03245467a",
  })



const code = new URLSearchParams(window.location.search).get('code');

function Interfaz({code}) {
    const accessToken = useAuth(code)
    const [search, setSearch] = useState("")
    const [searchResults, setSearchResults] = useState([])
    const [recomendacion,setRecomendacion] = useState([])
    const [semillas,setSemillas] = useState([])

    useEffect(() => {
        if (!accessToken) return
        spotifyApi.setAccessToken(accessToken)
        spotifyApi.getMySavedTracks({limit: 5, offset: 1}).then(res => {
            console.log(res)
            console.log(res.body.items)
            setSemillas(res.body.items)
        })
        
      }, [accessToken])
      
      
      
      console.log(semillas)


  return (
    <div className="App">
      
    <Header/>
    <Routes>
      <Route path="/" element={<div>{code}</div>}/>
      <Route index element={<div>{code}</div>}/>

    </Routes>

    </div>
  );
}

export default Interfaz;
