import React from "react";
import './App.css';
import Header from './Header';
import useAuth from './useAuth';
import {useState, useEffect} from 'react'
import useRecomendations from './useRecomendations';
import { Routes, Route, Link } from "react-router-dom";
import SpotifyWebApi from "spotify-web-api-node";

const spotifyApi = new SpotifyWebApi({
    clientId: "c176bd9d61984d3f8c4804b03245467a",
  })

const code = new URLSearchParams(window.location.search).get('code');

function Interfaz({code}) {
    const accessToken = useAuth(code)
    const [semillas,setSemillas] = useState([])
    const [recomendacion,setRecomendacion] = useState([])
    var arrSeeds = new Array();
    const [correr,setCorrer] = useState("")

    useEffect(() => {
        if (!accessToken) return
        spotifyApi.setAccessToken(accessToken)
        spotifyApi.getMySavedTracks({limit: 5, offset: 1}).then(res => {
            setSemillas(res.body.items.map(track => {
              return{
                semilla: track.track.artists[0].id
              }
            }))
        })
        setCorrer("True")
      }, [accessToken])

      const recomendationsGet = () => {
        if (!accessToken) return
        semillas.map(semilla => {
          arrSeeds.push(semilla.semilla)
          console.log(semilla.semilla)
        })  
        spotifyApi.setAccessToken(accessToken)
        spotifyApi.getRecommendations({min_energy: 0.4, seed_artists: arrSeeds, min_popularity: 50}).then(function(data) {
          let recommendations = data.body;
          console.log(recommendations.tracks);
          setRecomendacion(recommendations.tracks.map(tracks => {
            return {
              albumURL:tracks.album.images[0].url
            }
      
          }))
        }, function(err) {
          console.log("Something went wrong!", err);
        });
      }
    
    useEffect(() => {
      recomendationsGet();
    },[correr,accessToken])
  
  
    console.log(recomendacion)
  return (
    <div className="App">
      
    <Header/>
    <Routes>
      <Route path="*" element={
        <div> {arrSeeds.toString()}</div>
      
      
        }
      />
      

    </Routes>

    </div>
  );
}

export default Interfaz;
