import React from "react";
import './App.css';
import Header from './Header';
import useAuth from './useAuth';
import {useState, useEffect} from 'react'
import { Routes, Route, Link } from "react-router-dom";
import SpotifyWebApi from "spotify-web-api-node";
import TextField from '@mui/material/TextField';

const spotifyApi = new SpotifyWebApi({
    clientId: "c176bd9d61984d3f8c4804b03245467a",
  })



const code = new URLSearchParams(window.location.search).get('code');

function Interfaz({code}) {
    const accessToken = useAuth(code)
    const [recomendacion,setRecomendacion] = useState([])
    const [semillas,setSemillas] = useState([])

    useEffect(() => {
        if (!accessToken) return
        spotifyApi.setAccessToken(accessToken)
        /* spotifyApi.getMySavedTracks({limit: 5, offset: 1}).then(res => {
            console.log(res)
            console.log(res.body.items.track)
            setSemillas(res.body.items.map(track =>{
              return {
                semilla: track.artists[0].id
              }

            }))
        }) */
        
      }, [accessToken])
      
      useEffect(() => {
        if (!recomendacion) return setRecomendacion([])
        if (!accessToken) return
    
        let cancel = false
        spotifyApi.searchTracks(recomendacion).then(res => {
          if (cancel) return
          setRecomendacion(
            res.body.tracks.items.map(track => {
              const smallestAlbumImage = track.album.images.reduce(
                (smallest, image) => {
                  if (image.height < smallest.height) return image
                  return smallest
                },
                track.album.images[0]
              )
    
              return {
                artist: track.artists[0].name,
                title: track.name,
                uri: track.uri,
                albumUrl: smallestAlbumImage.url,
              }
            })
          )
        })
    
        return () => (cancel = true)
      }, [recomendacion, accessToken])

      

      console.log(semillas)
    /* useEffect(() => {
        spotifyApi.getMySavedTracks({limit: 5, offset: 1}).then(res => {
            console.log(res)
        })
    },[accessToken]) */

  return (
    <div className="App">
      
    <Header/>
    <TextField id="search" value={recomendacion} onChange={e => setRecomendacion(e.target.value)}/>
    <Routes>
      <Route path="/" element={<div>{code}</div>}/>
      <Route index element={<div>{code}</div>}/>

    </Routes>

    </div>
  );
}

export default Interfaz;
