import React from "react";
import './App.css';
import Header from './Header';
import useAuth from './useAuth';
import {useState, useEffect} from 'react'
import { Routes, Route, Link } from "react-router-dom";
import SpotifyWebApi from "spotify-web-api-node";

const spotifyApi = new SpotifyWebApi({
    clientId: "c176bd9d61984d3f8c4804b03245467a",
  })

const code = new URLSearchParams(window.location.search).get('code');

function Interfaz({code}) {
    const accessToken = useAuth(code)
    const [recomendacion,setRecomendacion] = useState([])
    const [semillas,setSemillas] = useState([])
    const [semillaStr1,setSemillaStr1] = useState("")
    const [semillaStr2,setSemillaStr2] = useState("")
    const [semillaStr3,setSemillaStr3] = useState("")
    const [semillaStr4,setSemillaStr4] = useState("")
    const [semillaStr5,setSemillaStr5] = useState("")
    const [contador,setContador] = useState("1")

    useEffect(() => {
        if (!accessToken) return
        spotifyApi.setAccessToken(accessToken)
        spotifyApi.getMySavedTracks({limit: 5, offset: 1}).then(res => {
            setSemillas(res.body.items.map(track => {
              return{
                semilla: track.track.artists[0].id
              }
            }))
        })
        

        /* spotifyApi.getRecommendations({min_energy:0.4,seed_artists: [semillas[0].semilla, semillas[1].semilla,semillas[2].semilla,semillas[3].semilla,semillas[4].semilla], min_popularity: 50}).then(res => {
          console.log(res)
        }) */
      }, [accessToken])
      console.log(semillas)
      semillas.map(semilla => {
        switch (contador){
          case 1:
            setSemillaStr1(semilla.semilla)
            contador = 2
            break;
          case 2:
            setSemillaStr2(semilla.semilla)
            contador = 3
            break;
          case 3:
            setSemillaStr3(semilla.semilla)
            contador = 4
            break;
          case 4:
            setSemillaStr4(semilla.semilla)
            contador = 5
            break;
          case 5:
            setSemillaStr5(semilla.semilla)
            break;
        }


        console.log(semilla.semilla)
        console.log(contador)
    })

    console.log(semillaStr1)
    console.log(semillaStr2)
    console.log(semillaStr3)
    console.log(semillaStr4)
    console.log(semillaStr5)

  return (
    <div className="App">
      
    <Header/>
    <Routes>
      <Route path="/" element={<div>{code}</div>}/>
      <Route index element={<div>{code}</div>}/>

    </Routes>

    </div>
  );
}

export default Interfaz;
